import React from "react";
import { Sidebar, Menu, MenuItem } from "react-pro-sidebar";
import GridViewOutlinedIcon from "@mui/icons-material/GridViewOutlined";
import VideoCameraFrontOutlinedIcon from "@mui/icons-material/VideoCameraFrontOutlined";
import ChatOutlinedIcon from "@mui/icons-material/ChatOutlined";
import FolderOutlinedIcon from "@mui/icons-material/FolderOutlined";
import CalendarMonthOutlinedIcon from "@mui/icons-material/CalendarMonthOutlined";
import Room from "../Room/Room";
import Lecture from "./Lecture";
import { Link } from "react-router-dom";

const MobileSidebar = ({
  isUserInRoom,
  subject,
  isSidebarOpen,
  toggleSidebar,
}) => {
  const components = {
    lecture: isUserInRoom ? <Room /> : <Lecture subjectId={subject.id} />,
    chat: null,
    files: null,
    calendar: null,
  };

  const MenuItemStyle = {
    display: "flex",
    flexDirection: "column",
  };

  const MenuStyle = {
    flexDirection: "row",
  };

  const sidebarStyle = {
    width: isSidebarOpen ? "250px" : "0",
    minWidth: isSidebarOpen ? "250px" : "0",
    transition: "width 0.5s",
  };

  return (
    <Sidebar className="mobileSidebar" style={sidebarStyle}>
      <Menu style={MenuStyle}>
        <MenuItem
          className="menu-item"
          component={<Link to="/dashboard" />}
          icon={<GridViewOutlinedIcon />}
          style={MenuItemStyle}>
          Dashboard
        </MenuItem>
        <MenuItem
          className="menu-item"
          icon={<VideoCameraFrontOutlinedIcon />}
          onClick={() => toggleSidebar("lecture")}
          style={MenuItemStyle}>
          Lecture
        </MenuItem>
        <MenuItem
          className="menu-item"
          icon={<ChatOutlinedIcon />}
          onClick={() => toggleSidebar("chat")}
          style={MenuItemStyle}>
          Chat
        </MenuItem>
        <MenuItem
          className="menu-item"
          icon={<FolderOutlinedIcon />}
          onClick={() => toggleSidebar("files")}
          style={MenuItemStyle}>
          Files
        </MenuItem>
        <MenuItem
          className="menu-item"
          icon={<CalendarMonthOutlinedIcon />}
          onClick={() => toggleSidebar("calendar")}
          style={MenuItemStyle}>
          Calendar
        </MenuItem>
      </Menu>
      {components[isSidebarOpen]}
    </Sidebar>
  );
};

export default MobileSidebar;
