import React, { useState } from "react";
import { styled } from "@mui/system";
import MainPageButton from "./MainPageButton";
import CreateRoomButton from "./CreateRoomButton";
import { connect } from "react-redux";
import ActiveRoomButton from "./ActiveRoomButton";

const MainContainer = styled("div")({
  width: "300px",
  height: "300px",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  backgroundColor: "white",
  margin: "6px",
  boxShadow: "0px 0px 4px gray",
  borderRadius: "40px",
});

const Lecture = ({ activeRooms, isUserInRoom, subjectId }) => {
  const [isVisible, setIsvisible] = useState(true);
  const handleActiveRoomClick = () => {
    setIsvisible(false);
  };
  return (
    <MainContainer>
      <CreateRoomButton isUserInRoom={isUserInRoom} subjectId={subjectId} />
      {activeRooms.map((room) => (
        <ActiveRoomButton
          roomId={room.roomId}
          creatorUsername={room.creatorUsername}
          amountOfParticipants={room.participants.length}
          key={room.roomId}
          isUserInRoom={isUserInRoom}
          onClick={handleActiveRoomClick}
        />
      ))}
    </MainContainer>
  );
};

const mapStoreStateToProps = ({ room }) => {
  return {
    ...room,
  };
};

export default connect(mapStoreStateToProps)(Lecture);
