import React, { useState } from "react";
import Wrapper from "../../shared/components/Wrapper";
import { Sidebar, Menu, MenuItem } from "react-pro-sidebar";
import GridViewOutlinedIcon from "@mui/icons-material/GridViewOutlined";
import VideoCameraFrontOutlinedIcon from "@mui/icons-material/VideoCameraFrontOutlined";
import ChatOutlinedIcon from "@mui/icons-material/ChatOutlined";
import FolderOutlinedIcon from "@mui/icons-material/FolderOutlined";
import CalendarMonthOutlinedIcon from "@mui/icons-material/CalendarMonthOutlined";
import LogoutOutlinedIcon from "@mui/icons-material/LogoutOutlined";
import Room from "../Room/Room";
import Lecture from "./Lecture";
import { Link } from "react-router-dom";
import { logout } from "../../shared/utils/auth";
import "../../shared/UI/css/Sidebar.css";

const SideBar = ({ isUserInRoom, subject }) => {
  const [selectMenuItem, setSelectMenuItem] = useState("dashboard");

  const handleMenuItemClick = (menuItem) => {
    setSelectMenuItem(menuItem);
  };

  const menuItemStyle = {
    color: selectMenuItem === "dashboard" ? "red" : "black", // Change color based on the selected state
  };

  return (
    <Wrapper>
      <Sidebar className="Sidebar">
        <div className="sidebarMenu">
          <h4>Data Structure ({subject.code})</h4>
          <Menu>
            <MenuItem
              className="menu-item"
              component={<Link to="/dashboard" />}
              icon={<GridViewOutlinedIcon style={menuItemStyle} />}
              onClick={() => handleMenuItemClick("dashboard")}>
              Dashboard
            </MenuItem>
            <MenuItem
              className="menu-item"
              icon={<VideoCameraFrontOutlinedIcon style={menuItemStyle} />}
              onClick={() => handleMenuItemClick("lecture")}>
              Lecture
            </MenuItem>
            {/* Add similar logic for other menu items */}
          </Menu>
        </div>
        {/* Sidebar Bottom Menu */}
        <div className="sidebarBottom">
          <Menu>
            <MenuItem
              className="menu-item"
              icon={<LogoutOutlinedIcon />}
              onClick={logout}>
              Logout
            </MenuItem>
          </Menu>
        </div>
      </Sidebar>
      {/* Render content based on the selected menu item */}
      {selectMenuItem === "lecture" ? (
        <Lecture subjectId={subject.id} />
      ) : (
        <Room />
      )}
    </Wrapper>
  );
};

export default SideBar;
