import React from "react";
import "../../shared/UI/css/chat.css";
import moment from "moment";

const Message = ({ message, username, timestamp }) => {
  const isFileMessage = message.fileUrl != null;

  return (
    <div className="message">
      <p>
        {username}: {message}
      </p>
      {isFileMessage && (
        <div>
          {message.fileType.startsWith("image/") ? (
            <img src={message.fileUrl} alt="Sent Image" />
          ) : (
            <a href={message.fileUrl} download>
              Download File
            </a>
          )}
        </div>
      )}
      <span>{moment(message.timestamp).format("MMMM Do, h:mm:ss a")}</span>
    </div>
  );
};

export default Message;
