import React, { useEffect, useState } from "react";
import { styled } from "@mui/system";
import SideBar from "./SideBar/SideBar";
import FriendsSideBar from "./FriendsSideBar/FriendsSideBar";
import SubjectSideBar from "./SubjectsSideBar/SubjectsSideBar";
import Spinner from "../shared/components/Spinner";
import AppBar from "./AppBar/AppBar";
import { logout } from "../shared/utils/auth";
import { connect } from "react-redux";
import { getActions } from "../store/actions/authActions";
import { connectWithSocketServer } from "../realtimeCommunication/socketConnection";
import Room from "./Room/Room";

// Styled components for spinner

const Wrapper = styled("div")({
  width: "100%",
  height: "100vh",
  display: "flex",
  backgroundColor: "#36393f",
});

const Dashboard = ({ setUserDetails, isUserInRoom }) => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const userDetails = localStorage.getItem("user");
    if (!userDetails) {
      logout();
    } else {
      const parsedUserDetails = JSON.parse(userDetails);
      setUserDetails(parsedUserDetails);
      connectWithSocketServer(parsedUserDetails);
      document.title = `Real Time Lecturing Dashboard \n ${parsedUserDetails.username}`;
      setIsLoading(false);
    }
  }, [setUserDetails]);

  if (isLoading) {
    return (
      <SpinnerWrapper>
        <Spinner />
      </SpinnerWrapper>
    );
  }

  return (
    <Wrapper>
      {/* <SideBar /> */}
      {/* <FriendsSideBar /> */}
      <SubjectSideBar />
      <AppBar />
      {isUserInRoom && <Room />}
    </Wrapper>
  );
};

const mapStoreStateToProps = ({ room }) => {
  return {
    ...room,
  };
};

const mapActionsToProps = (dispatch) => {
  return {
    ...getActions(dispatch),
  };
};

export default connect(mapStoreStateToProps, mapActionsToProps)(Dashboard);
