import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import "../../shared/UI/css/chat.css";
import * as socketConnection from "../../realtimeCommunication/socketConnection";
import moment from "moment";

const ChatFooter = () => {
  const [message, setMessage] = useState("");
  const [file, setFile] = useState(null);
  const userDetailsString = localStorage.getItem("user");
  const userDetails = JSON.parse(userDetailsString);
  const username = userDetails.username;

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSendMessage = () => {
    const fullMessage = {
      id: uuidv4(),
      username: username,
      message: message,
      timestamp: moment().format("MMMM Do, h:mm:ss a"),
    };

    const reader = new FileReader();
    reader.onloadend = () => {
      const fileBase64 = reader.result;
      const data = {
        message: message,
        file: fileBase64,
      };

      const serializedData = JSON.stringify(data);

      if (message.trim() !== "" || file) {
        socketConnection.sendMessage(serializedData);
        setMessage("");
        setFile(null);
      }
    };

    if (file) {
      reader.readAsDataURL(file);
    } else {
      const serializedData = JSON.stringify({ message: message });
      socketConnection.sendMessage(serializedData);
      setMessage("");
    }
  };

  return (
    <div className="chat-footer">
      <input
        type="text"
        placeholder="Send a message"
        value={message}
        onChange={handleMessageChange}
      />
      <input type="file" onChange={handleFileChange} />
      <button className="send-btn" onClick={handleSendMessage}>
        Send
      </button>
    </div>
  );
};

export default ChatFooter;
