import React, { useState } from "react";
import "../../shared/UI/css/OrganizationsAndSubjects.css";

const styleCreateOrganization = {
  display: "flex",
  flexDirection: "column",
  padding: "10px 15px",
  width: "100%",
  justifyContent: "center",
  alignItems: "center",
};

const CreateOrganization = ({ onOrganizationCreate }) => {
  const [subjectCount, setSubjectCount] = useState(1);
  const [organizationData, setOrganizationData] = useState({
    name: "",
    subjects: [],
    adminName: "",
  });

  const handleAddBtn = () => {
    setSubjectCount(subjectCount + 1);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrganizationData((prevDate) => ({
      ...prevDate,
      [name]: value,
    }));
  };
  const handleCreate = () => {
    if (organizationData.name.trim() !== "") {
      onOrganizationCreate(organizationData);
    }
  };
  return (
    <div className="create-organization" style={styleCreateOrganization}>
      <input
        type="text"
        placeholder="Oraganization name"
        value={organizationData.name}
        onChange={handleInputChange}
      />
      {Array.from({ length: subjectCount }).map((_, index) => (
        <input
          key={index}
          type="text"
          placeholder={`Subject ${index + 1}`}
          onChange={handleInputChange}
        />
      ))}
      {/*  />
      <input type="text" placeholder="Subject 2" /> */}
      <button onClick={handleAddBtn}>+</button>
      <input
        type="text"
        placeholder="Admin name"
        value={organizationData.adminName}
        onChange={handleInputChange}
      />
      <button>Create</button>
    </div>
  );
};

export default CreateOrganization;
