import React, { useState, useEffect } from "react";
import Spinner from "../shared/components/Spinner";
import Lectures from "./Lectures/Lectures";
import { connect } from "react-redux";
import { getActions } from "../store/actions/authActions";
import { connectWithSocketServer } from "../realtimeCommunication/socketConnection";
import { logout } from "../shared/utils/auth";
import "../shared/UI/css/dashboard.css";
import DashboardHeader from "../shared/components/DashboardHeader";
import { styled } from "@mui/system";
import Organizations from "./Organizations/Organizations";
import Subjects from "./Subjects/Subjects";

const Wrapper = styled("div")({
  display: "grid",
  gridTemplateColumns: "1fr 1fr 1fr",
  height: "100vh",
});

const Dashboard = ({ setUserDetails, socketOpen }) => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const userDetails = localStorage.getItem("user");
    if (!userDetails) {
      logout();
    } else {
      const parsedUserDetails = JSON.parse(userDetails);
      setUserDetails(parsedUserDetails);
      if (!socketOpen) {
        connectWithSocketServer(parsedUserDetails);
      }
      setIsLoading(false);
    }
  }, [setUserDetails, socketOpen]);

  if (isLoading) {
    return <Spinner />;
  }
  return (
    <>
      <DashboardHeader />
      <Wrapper>
        <Subjects />
        <Lectures />
        <Organizations />
      </Wrapper>
    </>
  );
};

const mapActionsToProps = (dispatch) => {
  return {
    ...getActions(dispatch),
  };
};

const mapStoreStateToProps = ({ socket }) => {
  return {
    socketOpen: socket.socketOpen,
  };
};

export default connect(mapStoreStateToProps, mapActionsToProps)(Dashboard);
