export const logout = () => {
  localStorage.clear();
  window.location.pathname = "/login";
};

export const useCheckUserDetails = (setIsLoading) => {
  const userDetails = localStorage.getItem("user");
  if (!userDetails) {
    logout();
  } else {
    setIsLoading(false);
  }
};
