import React, { useEffect, useState } from "react";
import SideBar from "./SideBar/Lecture";
import { connect } from "react-redux";
import { getActions } from "../store/actions/authActions";
import Room from "./Room/Room";
import Wrapper from "../shared/components/Wrapper";
import Spinner from "../shared/components/Spinner";
import { useParams } from "react-router-dom";
import Files from "./SubjectBar/Files";
import { connectWithSocketServer } from "../realtimeCommunication/socketConnection";
import { logout } from "../shared/utils/auth";
import DashboardHeader from "../shared/components/DashboardHeader";
import { Link } from "react-router-dom";
import { Sidebar, Menu, MenuItem, SubMenu } from "react-pro-sidebar";
import GridViewOutlinedIcon from "@mui/icons-material/GridViewOutlined";
import VideoCameraFrontOutlinedIcon from "@mui/icons-material/VideoCameraFrontOutlined";
import ChatOutlinedIcon from "@mui/icons-material/ChatOutlined";
import FolderOutlinedIcon from "@mui/icons-material/FolderOutlined";
import CalendarMonthOutlinedIcon from "@mui/icons-material/CalendarMonthOutlined";
import "../shared/UI/css/SubjectPage.css";

const Subject = ({ subjects, setUserDetails, room, socketOpen }) => {
  const [isLoading, setIsLoading] = useState(false);
  console.log(subjects.subjects);
  const isUserInRoom = room.isUserInRoom;
  useEffect(() => {
    const userDetails = localStorage.getItem("user");
    if (!userDetails) {
      logout();
    } else {
      const parsedUserDetails = JSON.parse(userDetails);
      setUserDetails(parsedUserDetails);
      if (!socketOpen && !subjects.subjects.length) {
        connectWithSocketServer(parsedUserDetails);
      }
      setIsLoading(false);
    }
  }, [setUserDetails, socketOpen, subjects.subjects.length]);

  const { id } = useParams();

  const subject = subjects.subjects.find((subject) => subject.code === id);
  console.log("subject ", subject);
  if (!subjects.subjects.length) {
    return <Spinner />;
  }
  if (isLoading) {
    return <Spinner />;
  }

  return (
    <>
      <div className="header-container">
        <DashboardHeader />
      </div>
      <Wrapper>
        <Sidebar className="Sidebar" style={{ backgroundColor: "white" }}>
          <h4 style={{ textAlign: "center" }}>
            Data Structure ({subject.code})
          </h4>
          <Menu>
            <MenuItem
              className="menu-item"
              component={<Link to="/dashboard" />}
              icon={<GridViewOutlinedIcon />}>
              Dashboard
            </MenuItem>
            <MenuItem
              className="menu-item"
              icon={<VideoCameraFrontOutlinedIcon />}>
              Lecture
            </MenuItem>
            <MenuItem className="menu-item" icon={<ChatOutlinedIcon />}>
              Chat
            </MenuItem>
            <MenuItem className="menu-item" icon={<FolderOutlinedIcon />}>
              Files
            </MenuItem>
            <MenuItem
              className="menu-item"
              icon={<CalendarMonthOutlinedIcon />}>
              Calendar
            </MenuItem>
          </Menu>
        </Sidebar>
        {/* <SideBar subjectId={subject.id} /> */}
        {/* <Files /> */}
        {isUserInRoom && <Room />}
      </Wrapper>
    </>
  );
};

const mapStoreStateToProps = ({ room, subjects, socket }) => {
  return {
    subjects,
    room,
    socketOpen: socket.socketOpen,
  };
};

const mapActionsToProps = (dispatch) => {
  return {
    ...getActions(dispatch),
  };
};

export default connect(mapStoreStateToProps, mapActionsToProps)(Subject);
