import React from "react";
import OtpInput from "react-otp-input";
import "../../shared/UI/css/style.css";
import AuthBox from "../../shared/components/AuthBox";
const VerificationPage = ({
  handleVerify,
  verificationCode,
  setVerificationCode,
}) => {
  const otpInputStyle = {
    width: "40px",
    height: "40px",
    fontSize: "20px",
    margin: "5px",
    border: "2px solid",
    borderRadius: "10px",
    textAlign: "center",
    fontWeight: "800",
  };
  const handleClickVerificationButton = (e) => {
    e.preventDefault();
    handleVerify();
  };

  return (
    <AuthBox>
      <OtpInput
        value={verificationCode}
        onChange={setVerificationCode}
        numInputs={6}
        renderSeparator={<span>-</span>}
        renderInput={(props, index) => (
          <input {...props} style={otpInputStyle} />
        )}
      />
      <button className="submitBtn" onClick={handleClickVerificationButton}>
        Submit
        <svg fill="white" viewBox="0 0 448 512" height="1em" className="arrow">
          <path d="M438.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-160-160c-12.5-12.5-32.8-12.5-45.3 0s-12.5 32.8 0 45.3L338.8 224 32 224c-17.7 0-32 14.3-32 32s14.3 32 32 32l306.7 0L233.4 393.4c-12.5 12.5-12.5 32.8 0 45.3s32.8 12.5 45.3 0l160-160z"></path>
        </svg>
      </button>
    </AuthBox>
  );
};

export default VerificationPage;
