import React, { useState } from "react";
import { Link, useLocation, useHistory } from "react-router-dom";
import "../UI/css/navigator.css";

const Navigator = () => {
  const location = useLocation();
  const history = useHistory();
  const [messagesActive, setMessagesActive] = useState(false);

  const handleDashboardClick = () => {
    // Navigate to the Dashboard page
    history.push("/dashboard");
  };

  const handleMessagesClick = () => {
    // Navigate to the Messages page
    history.push("/messages");
    // Set the messagesActive state to true
    setMessagesActive(true);
  };

  return (
    <div className="Navigator-container">
      <div className="header">
        <span className="word-on-top">Your Word Here</span>
      </div>
      <div
        className={`dashboard ${
          location.pathname === "/dashboard" && "active"
        }`}
        onClick={handleDashboardClick}>
        <span className="material-symbols-outlined">grid_view</span>
        <span
          className={`dashboard-btn ${
            location.pathname === "/dashboard" && "active"
          }`}>
          Dashboard
        </span>
      </div>
      <div
        className={`messages ${
          location.pathname === "/messages" || messagesActive ? "active" : ""
        }`}
        onClick={handleMessagesClick}>
        <span className="material-symbols-outlined">chat</span>
        <span
          className={`messages-btn ${
            location.pathname === "/messages" || messagesActive ? "active" : ""
          }`}>
          Messages
        </span>
      </div>
    </div>
  );
};

export default Navigator;
