//   socket: socketReducer,
//   chat: chatReducer,
// });

// const store = createStore(
//   rootReducer,
//   composeWithDevTools(applyMiddleware(thunk))
// );
const store = configureStore({
  reducer: {
    auth: authSlice,
    alert: alertSlice,
    socket: socketSlice,
    room: roomSlice,
    subjects: subjectsSlice,
  },
});

export default store;
