import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../UI/css/header.css";
import { logout } from "../utils/auth";
import GridViewOutlinedIcon from "@mui/icons-material/GridViewOutlined";
import VideoCameraFrontOutlinedIcon from "@mui/icons-material/VideoCameraFrontOutlined";
import ChatOutlinedIcon from "@mui/icons-material/ChatOutlined";
import FolderOutlinedIcon from "@mui/icons-material/FolderOutlined";
import CalendarMonthOutlinedIcon from "@mui/icons-material/CalendarMonthOutlined";
import { getActions } from "../../store/actions/authActions";
import { Sidebar, Menu, MenuItem } from "react-pro-sidebar";
import Room from "../../Dashboard/Room/Room";
import Lecture from "../../Dashboard/Lecture/Lecture";
import { connect } from "react-redux";

const DashboardNavigation = ({ isClicked, setIsClicked }) => {
  useEffect(() => {
    const handleOutsideClick = (e) => {
      if (isClicked && !e.target.closest(".navUl")) {
        setIsClicked(false);
      }
    };
    document.addEventListener("click", handleOutsideClick);
    return () => {
      document.removeEventListener("click", handleOutsideClick);
    };
  }, [isClicked, setIsClicked]);

  const components = {
    lecture: isUserInRoom ? <Room /> : <Lecture subjectId={subject.id} />,
    chat: null,
    files: null,
    calendar: null,
  };

  return (
    <div className="navigation">
      <ul className={isClicked ? "navUl-active" : "navUl"}>
        <li>
          <Link className="a" to="/">
            Home
          </Link>
        </li>
        <li>
          <Link className="a" to="/" onClick={logout}>
            Logout
          </Link>
        </li>
        <Sidebar
          className="mobileSidebar"
          style={{
            backgroundColor: "white",
            marginTop: "1px",
            display: "flex",
            flexDirection: "row",
          }}>
          <Menu style={MenuStyle}>
            <MenuItem
              className="menu-item"
              component={<Link to="/dashboard" />}
              icon={<GridViewOutlinedIcon />}
              style={MenuItemStyle}>
              Dashboard
            </MenuItem>
            <MenuItem
              className="menu-item"
              icon={<VideoCameraFrontOutlinedIcon />}
              onClick={() => {
                setSelectMenuItem("lecture");
              }}
              style={MenuItemStyle}>
              Lecture
            </MenuItem>
            <MenuItem
              className="menu-item"
              icon={<ChatOutlinedIcon />}
              onClick={() => setSelectMenuItem("chat")}
              style={MenuItemStyle}>
              Chat
            </MenuItem>
            <MenuItem
              className="menu-item"
              icon={<FolderOutlinedIcon />}
              onClick={() => setSelectMenuItem("files")}
              style={MenuItemStyle}>
              Files
            </MenuItem>
            <MenuItem
              className="menu-item"
              icon={<CalendarMonthOutlinedIcon />}
              onClick={() => setSelectMenuItem("calendar")}
              style={MenuItemStyle}>
              Calendar
            </MenuItem>
          </Menu>
        </Sidebar>
        {components[selectMenuItem]}
      </ul>
    </div>
  );
};

const mapStoreStateToProps = ({ room, subjects, socket }) => {
  return {
    subjects,
    room,
    socketOpen: socket.socketOpen,
  };
};

const mapActionsToProps = (dispatch) => {
  return {
    ...getActions(dispatch),
  };
};

export default connect(
  mapStoreStateToProps,
  mapActionsToProps
)(DashboardNavigation);
