import React, { useEffect, useState } from "react";
import SideBar from "./SideBar/SideBar";
import AppBar from "./AppBar/AppBar";
import { connect } from "react-redux";
import { getActions } from "../store/actions/authActions";
import Room from "./Room/Room";
import useUserDetails from "../shared/utils/useUserDetails";
import Wrapper from "../shared/components/Wrapper";
import Spinner from "../shared/components/Spinner";
import { useParams } from "react-router-dom";
import Files from "./SubjectBar/Files";
import { connectWithSocketServer } from "../realtimeCommunication/socketConnection";
import { logout } from "../shared/utils/auth";
import store from "../store/store";

const Subject = ({ subjects, setUserDetails, isUserInRoom }) => {
  const [isLoading, setIsLoading] = useState(false);
  console.log(subjects.subjects);

  // useEffect(() => {
  //   const userDetails = localStorage.getItem("user");
  //   if (!userDetails) {
  //     logout();
  //   } else {
  //     const parsedUserDetails = JSON.parse(userDetails);
  //     setUserDetails(parsedUserDetails);
  //     if (subjects.subjects.length) {
  //       connectWithSocketServer(parsedUserDetails);
  //     }
  //     setIsLoading(false);
  //   }
  // }, [setUserDetails, setIsLoading]);

  const { id } = useParams();

  const subject = subjects.subjects.find((subject) => subject.code === id);
  console.log("subject ", subject);
  if (!subjects.subjects.length) {
    return <Spinner />;
  }
  if (isLoading) {
    return <Spinner />;
  }

  return (
    <Wrapper>
      <SideBar subjectId={subject.id} />
      <Files />
      <AppBar />
      <h1>{subject.code}</h1>
      {isUserInRoom && <Room />}
    </Wrapper>
  );
};

const mapStoreStateToProps = ({ room, subjects }) => {
  return {
    subjects,
    room,
  };
};

const mapActionsToProps = (dispatch) => {
  return {
    ...getActions(dispatch),
  };
};

export default connect(mapStoreStateToProps, mapActionsToProps)(Subject);
