import React from "react";
import { styled } from "@mui/system";

const Wrapper = styled("div")({
  display: "flex",
  flexDirection: "column",
  width: "100%",
  marginBottom: "20px", // Add some bottom margin for spacing
});

const Label = styled("p")({
  color: "#555", // Adjust label color
  textTransform: "uppercase",
  fontWeight: "600",
  fontSize: "14px", // Adjust font size
  marginBottom: "8px", // Add some bottom margin for spacing
});

const Input = styled("input")({
  height: "40px",
  border: "1px solid #ddd", // Adjust border color
  borderRadius: "5px",
  color: "#555", // Adjust text color
  background: "#f8f8f8", // Adjust background color
  margin: 0,
  fontSize: "16px",
  padding: "0 12px", // Add some padding for a cleaner look
  transition: "border-color 0.3s ease", // Add transition for smoother effects

  "&:focus": {
    outline: "none",
    borderColor: "#2196f3", // Adjust border color on focus
  },
});

const InputWithLabel = (props) => {
  const { value, setValue, label, type, placeholder } = props;

  const handleValueChange = (event) => {
    setValue(event.target.value);
  };

  return (
    <Wrapper>
      <Label>{label}</Label>
      <Input
        value={value}
        onChange={handleValueChange}
        type={type}
        placeholder={placeholder}
      />
    </Wrapper>
  );
};

export default InputWithLabel;
