import React, { useState } from "react";
import { styled } from "@mui/system";
import "../../shared/UI/css/OrganizationsAndSubjects.css";
import CreateOrganization from "./CreateOrganization";

const Wrapper = styled("div")({
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  backgroundColor: "white",
  height: "calc(100vh - 107px)",
  position: "relative",
  borderRadius: "40px",
  padding: "20px",
  margin: "5px",
  boxShadow: "0px 0px 5px #999999",
});

const Organizations = () => {
  const [organizations, setOrganizations] = useState([
    {
      name: "FCI Assiut University",
      subjects: ["Operating System", "Data Structures"],
    },
  ]);

  const [activeAccordion, setActiveAccordion] = useState(null);
  const [createOrg, setCreateOrg] = useState(false);

  const handleAccordion = (index) => {
    setActiveAccordion((prev) => (prev === index ? null : index));
  };

  const handleCreateOrganization = () => {
    setCreateOrg(!createOrg);
  };

  const handleCreateOrganizationSubmit = (organizationData) => {
    setOrganizations((prev) => [...prev, organizationData]);
    setCreateOrg(false);
  };

  return (
    <Wrapper>
      <h2 styled={{ padding: "5px", margin: "10px" }}>Organizations</h2>

      {/* Render existing organizations */}
      {organizations.map((organization, index) => (
        <div
          key={index}
          className={`organization-name ${
            activeAccordion === index ? "active" : ""
          }`}
          onClick={() => handleAccordion(index)}>
          {organization.name}
        </div>
      ))}

      {/* Render the active accordion's content */}
      {activeAccordion !== null && (
        <div className={`content ${activeAccordion !== null ? "active" : ""}`}>
          {organizations[activeAccordion].subjects.map((subject, index) => (
            <span key={index}>{subject}</span>
          ))}
        </div>
      )}

      {createOrg ? (
        <>
          <CreateOrganization onSubmit={handleCreateOrganizationSubmit} />
          <button onClick={handleCreateOrganization}>Cancel</button>
        </>
      ) : (
        <button onClick={handleCreateOrganization}>Create Organization</button>
      )}
    </Wrapper>
  );
};

export default Organizations;
