import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import "../../shared/UI/css/chat.css";
import * as socketConnection from "../../realtimeCommunication/socketConnection";
import moment from "moment";

const ChatFooter = () => {
  const [message, setMessage] = useState("");
  const [file, setFile] = useState(null);
  const userDetailsString = localStorage.getItem("user");
  const userDetails = JSON.parse(userDetailsString);
  const username = userDetails.username;

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSendMessage = () => {
    const fullMessage = {
      id: uuidv4(),
      username: username,
      message: message,
      timestamp: moment().format("YYYY-MM-DD HH:mm:ss"),
    };

    if (file) {
      const formatData = new FormData();
      formatData.append("file", file);
      formatData.append("message", JSON.stringify(fullMessage));

      fetch("/upload", {
        method: "POST",
        body: formatData,
      })
        .then((response) => response.json())
        .then((data) => {
          socketConnection.sendMessage(data);
        });
    }
    if (message.trim() !== "") {
      socketConnection.sendMessage(fullMessage);
      setMessage("");
    }
  };

  return (
    <div className="chat-footer">
      <input
        type="text"
        placeholder="Send a message"
        value={message}
        onChange={handleMessageChange}
      />
      <input type="file" onChange={handleFileChange} />
      <button className="send-btn" onClick={handleSendMessage}>
        Send
      </button>
    </div>
  );
};

export default ChatFooter;
