import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import "../../shared/UI/css/chat.css";
import * as socketConnection from "../../realtimeCommunication/socketConnection";
import moment from "moment";
import { useDropzone } from "react-dropzone";
import axios from "axios";

const ENDPOINT = "http://localhost:4000";

const ChatFooter = () => {
  const [message, setMessage] = useState("");
  const [file, setFile] = useState(null);
  const userDetailsString = localStorage.getItem("user");
  const userDetails = JSON.parse(userDetailsString);
  const username = userDetails.username;

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const onDrop = (acceptedFiles) => {
    setFile(acceptedFiles[0]);
  };

  const handleSendMessage = async () => {
    if (message.trim() === "" && !file) return;

    const fullMessage = {
      id: uuidv4(),
      username: username,
      message: message,
      timestamp: moment().format("MMMM Do, h:mm:ss a"),
    };

    if (file) {
      const formData = new FormData();
      formData.append("file", file);

      try {
        const response = await axios.post(`${ENDPOINT}/upload`, formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        });

        fullMessage.file = {
          filename: response.data.filename,
          url: response.data.url,
        };
      } catch (error) {
        console.error("Error uploading file:", error);
      }
    }

    socketConnection.sendMessage(JSON.stringify(fullMessage));
    setMessage("");
    setFile(null);
  };

  const { getRootProps, getInputProps } = useDropzone({ onDrop });

  return (
    <div className="chat-footer">
      <input
        type="text"
        placeholder="Send a message"
        value={message}
        onChange={handleMessageChange}
      />
      <div {...getRootProps()} className="dropzone">
        <input {...getInputProps()} />
        <p>select a file</p>
      </div>
      <button className="send-btn" onClick={handleSendMessage}>
        Send
      </button>
    </div>
  );
};

export default ChatFooter;
