import React, { useState } from "react";
import Wrapper from "../../shared/components/Wrapper";
import { Sidebar, Menu, MenuItem } from "react-pro-sidebar";
import GridViewOutlinedIcon from "@mui/icons-material/GridViewOutlined";
import VideoCameraFrontOutlinedIcon from "@mui/icons-material/VideoCameraFrontOutlined";
import ChatOutlinedIcon from "@mui/icons-material/ChatOutlined";
import FolderOutlinedIcon from "@mui/icons-material/FolderOutlined";
import CalendarMonthOutlinedIcon from "@mui/icons-material/CalendarMonthOutlined";
import Room from "../Room/Room";
import Lecture from "./Lecture";
import { Link } from "react-router-dom";
import DashboardHeader from "../../shared/components/DashboardHeader";

const RegularHeaderAndSidebar = ({ isUserInRoom, subject }) => {
  const [selectMenuItem, setSelectMenuItem] = useState("dashboard");
  const [isOpen, setIsOpen] = useState(true); // Set the initial state to true

  const components = {
    lecture: isUserInRoom ? <Room /> : <Lecture subjectId={subject.id} />,
    chat: null,
    files: null,
    calendar: null,
  };

  const handleToggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleCloseSidebar = () => {
    setIsOpen(false);
  };

  return (
    <>
      <DashboardHeader />
      <Wrapper>
        <Sidebar
          className="Sidebar"
          style={{
            backgroundColor: "white",
            marginTop: "1px",
            width: isOpen ? "250px" : "75px",
          }}
          collapsed={!isOpen} // Add this line to handle collapse state
        >
          <h4
            style={{ textAlign: "center", cursor: "pointer" }}
            onClick={handleToggleSidebar}>
            Data Structure ({subject.code})
          </h4>
          <h1 onClick={handleCloseSidebar}>a7a</h1>
          <Menu>{/* ... (unchanged) */}</Menu>
        </Sidebar>
        {components[selectMenuItem]}
      </Wrapper>
    </>
  );
};

export default RegularHeaderAndSidebar;
