import React, { useEffect, useState } from "react";
import SideBar from "./SideBar/SideBar";
import AppBar from "./AppBar/AppBar";
import { connect } from "react-redux";
import { getActions } from "../store/actions/authActions";
import Room from "./Room/Room";
import { checkUserDetails } from "../shared/utils/auth";
import Wrapper from "../shared/components/Wrapper";
import Spinner from "../shared/components/Spinner";
import { useParams } from "react-router-dom";
import Files from "./SubjectBar/Files";

const Subject = ({ isUserInRoom, subjects }) => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Call useCheckUserDetails only once when the component mounts
    checkUserDetails(setIsLoading);
  }, []); // Empty dependency array ensures this effect runs only once

  const { id } = useParams();

  const subject = subjects.find((subject) => subject.code === id);

  if (isLoading || !subject) {
    return <Spinner />;
  }

  return (
    <Wrapper>
      <SideBar />
      <Files />
      <AppBar subjectCode={subject.code} />
      <h1>{subject.code}</h1>
      {isUserInRoom && <Room />}
    </Wrapper>
  );
};

const mapStoreStateToProps = ({ room, subjects }) => {
  return {
    ...room,
    ...subjects,
  };
};

const mapActionsToProps = (dispatch) => {
  return {
    ...getActions(dispatch),
  };
};

export default connect(mapStoreStateToProps, mapActionsToProps)(Subject);
